    static String encryptString(String S) {
        int[] counts = new int[26];
        StringBuilder newsb = new StringBuilder();

        for (int i = 0; i < S.length(); i++) {
            char c = S.charAt(i);
            if (c >= 'a' && c <= 'z') {

                counts[c - 'a']++;
//                System.out.println(c);
            }
        }
        for (int e : counts
        ) {
            System.out.println("e" + e);

        }

        StringBuilder stringBuilder = new StringBuilder();
        for (char c = 'a'; c <= 'z'; c++) {
            int count = counts[c - 'a'];
            if (count > 0) {

                stringBuilder.append(c);
                stringBuilder.append(Integer.toString(count, 16));


            }
        }

        return stringBuilder.toString();
    }

    static String encryptString2(String S) {
        StringBuilder sb = new StringBuilder();
        HashMap<Character, Integer> map = new HashMap<>();
        int count = 1;

        for (int i = 0; i < S.length(); i++) {
            if (!map.containsKey(S.charAt(i))) {
                map.put(S.charAt(i), count);
            } else {
                map.put(S.charAt(i), map.getOrDefault(S.charAt(i), 0) + 1);
            }
        }
        System.out.println(map);

        for (Map.Entry<Character, Integer> entry : map.entrySet()) {
            sb.append(entry.getKey()).append(Integer.toHexString(entry.getValue()));
        }

        return sb.toString();
    }

    //    21fz2a3u365s215p18eo161n12l2aef2f5b4a
    static String encryptString3(String S) {

        long count = 1;
        String str = "";

        for (int i = 0; i < S.length() - 1; i++) {
            if (S.charAt(i) == S.charAt(i + 1)) {
                count++;
            } else {
                str = Long.toHexString(count) + S.charAt(i) + str;
                count = 1;
            }
        }
        str = Long.toHexString(count) + S.charAt(S.length() - 1) + str;

        return str;
    }

